ritos;$qry_eval = "select count(*) cantidad ,avg(confianza) confia ,avg(calidad) calida,avg(disponibilidad) diaponible,avg(planeacion) planea from evaluaprof where rfc = '$user' and periodo = '$periodo'";$res_eval = ejecuta_consulta($qry_eval);while($fila = $res_eval->fetch_assoc()){	$confianza 		= $fila['confia'];	$calidad 		= $fila['calida'];	$disponibilidad = $fila['diaponible'];	$planeacion 	= $fila['planea'];}	$confianza = ($confianza * 100) / 5;	$calidad = ($calidad * 100) / 5;	$disponibilidad = ($disponibilidad * 100) / 5;	$planeacion = ($planeacion * 100) / 5;    echo "<tr id='$id'> <td align='center'> ".bcdiv($confianza,'1',0)." %</td> <td align='center'> ".bcdiv($calidad,'1',0)." %</td>	<td align='center'> ".bcdiv($disponibilidad,'1',0)." %</td> <td align='center'> ".bcdiv($planeacion,'1',0)." %</td>    </tr>";    echo "<h2 align='center'>EL Porcentaje de alumnos que evaluaron es de  ".bcdiv($solo,'1',0)."% siendo el minimo requerido de 70%</h2>";?>  </div>